import React, { useState, useEffect } from 'react';

const questions = [
  {
    question: 'Apa ibukota Indonesia?',
    options: ['Jakarta', 'Surabaya', 'Bandung', 'Medan'],
    correctAnswer: 'Jakarta',
  },
  {
    question: 'Siapakah presiden pertama Indonesia?',
    options: ['Soekarno', 'Soeharto', 'Megawati', 'Jokowi'],
    correctAnswer: 'Soekarno',
  },
  {
    question: 'Berapakah hasil dari 10 + 5?',
    options: ['12', '15', '18', '20'],
    correctAnswer: '15',
  },
  {
    question: 'Apa warna langit?',
    options: ['Merah', 'Biru', 'Hijau', 'Kuning'],
    correctAnswer: 'Biru',
  },
  {
    question: 'Siapakah penemu gravitasi?',
    options: ['Isaac Newton', 'Albert Einstein', 'Galileo Galilei', 'Nikola Tesla'],
    correctAnswer: 'Isaac Newton',
  },
];

const App = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [score, setScore] = useState(0);
  const [time, setTime] = useState(20);

  useEffect(() => {
    const timer = setInterval(() => {
      if (time > 0) {
        setTime(prevTime => prevTime - 1);
      } else {
        handleNextQuestion();
      }
    }, 1000);

    return () => clearInterval(timer);
  }, [time]);

  const handleOptionClick = option => {
    if (selectedOption === null) {
      setSelectedOption(option);

      if (option === questions[currentQuestion].correctAnswer) {
        setScore(prevScore => prevScore + 20);
      }
    }
  };

  const handleNextQuestion = () => {
    setSelectedOption(null);
    setTime(20);

    if (currentQuestion < questions.length - 1) {
      setCurrentQuestion(prevQuestion => prevQuestion + 1);
    } else {
      // Game selesai, mungkin Anda ingin menambahkan logika di sini
      alert(`Game selesai! Skor Anda: ${score}`);
    }
  };

  return (
    <div>
      <h1>Quiz Game</h1>
      <p>Waktu Tersisa: {time} detik</p>
      <h2>Skor: {score}</h2>
      <div>
        <p>{questions[currentQuestion].question}</p>
        {questions[currentQuestion].options.map((option, index) => (
          <button
            key={index}
            onClick={() => handleOptionClick(option)}
            style={{
              backgroundColor:
                selectedOption === option
                  ? option === questions[currentQuestion].correctAnswer
                    ? 'green'
                    : 'red'
                  : 'white',
            }}
            disabled={selectedOption !== null}
          >
            {option}
          </button>
        ))}
      </div>
      <button onClick={handleNextQuestion} disabled={selectedOption === null}>
        Lanjutkan
      </button>
    </div>
  );
};

export default App;
