import React, { useState } from 'react';

const Quiz = () => {
  const questions = [
    {
      question: 'Apa ibu kota Indonesia?',
      options: ['Jakarta', 'Bandung', 'Surabaya', 'Semarang'],
      answerIndex: 0,
    },
    // Tambahkan pertanyaan lain di sini
  ];

  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [selectedOption, setSelectedOption] = useState(null);
  const [score, setScore] = useState(0);

  const handleOptionSelect = (selectedIdx) => {
    const correctAnswerIdx = questions[currentQuestion].answerIndex;
    setSelectedOption(selectedIdx);

    if (selectedIdx === correctAnswerIdx) {
      setScore(score + 20);
    }
  };

  const handleNextQuestion = () => {
    setSelectedOption(null);
    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
    } else {
      // Jika sudah jawab semua pertanyaan, lakukan sesuatu (misalnya, tampilkan skor total)
      alert(`Skor Anda: ${score}`);
    }
  };

  return (
    <div className="quiz-container">
      <h2>{questions[currentQuestion].question}</h2>
      <ul>
        {questions[currentQuestion].options.map((option, index) => (
          <li
            key={index}
            onClick={() => handleOptionSelect(index)}
            style={{
              backgroundColor:
                selectedOption === index
                  ? index === questions[currentQuestion].answerIndex
                    ? 'green' // Jawaban benar dipilih
                    : 'red' // Jawaban salah dipilih
                  : 'white', // Belum dipilih
            }}
          >
            {option}
          </li>
        ))}
      </ul>
      <button onClick={handleNextQuestion}>Next</button>
    </div>
  );
};

export default Quiz;
