import React, { useState } from 'react';

const questions = [
  {
    question: 'Apa ibukota Indonesia?',
    options: ['Jakarta', 'Bandung', 'Surabaya', 'Medan'],
    answer: 'Jakarta',
  },
  {
    question: 'Siapakah presiden pertama Indonesia?',
    options: ['Soekarno', 'Soeharto', 'Jokowi', 'Megawati'],
    answer: 'Soekarno',
  },
  {
    question: 'Berapa jumlah provinsi di Indonesia saat ini?',
    options: ['30', '32', '34', '36'],
    answer: '34',
  },
  {
    question: 'Apa warna bendera Indonesia?',
    options: ['Merah-Putih', 'Hijau-Kuning', 'Biru-Putih', 'Merah-Biru'],
    answer: 'Merah-Putih',
  },
  {
    question: 'Siapakah penemu bola lampu?',
    options: ['Edison', 'Newton', 'Einstein', 'Tesla'],
    answer: 'Edison',
  },
];

const App = () => {
  const [currentQuestion, setCurrentQuestion] = useState(0);
  const [showResult, setShowResult] = useState(false);
  const [score, setScore] = useState(0);
  const [selectedOption, setSelectedOption] = useState('');

  const handleOptionClick = (selectedAnswer) => {
    setSelectedOption(selectedAnswer);

    if (selectedAnswer === questions[currentQuestion].answer) {
      setScore(score + 1);
    }

    setShowResult(true);
  };

  const nextQuestion = () => {
    if (currentQuestion + 1 < questions.length) {
      setCurrentQuestion(currentQuestion + 1);
      setShowResult(false);
      setSelectedOption('');
    } else {
      setShowResult(true);
    }
  };

  return (
    <div className="App">
      <h1>Quiz Game</h1>
      {showResult ? (
        <div>
          <p>
            Jawaban yang benar adalah: {questions[currentQuestion].answer}
          </p>
          {currentQuestion + 1 < questions.length ? (
            <button onClick={nextQuestion}>Soal Berikutnya</button>
          ) : (
            <p>
              Total skor Anda: {score} dari {questions.length}
            </p>
          )}
        </div>
      ) : (
        <div>
          <h2>{questions[currentQuestion].question}</h2>
          {questions[currentQuestion].options.map((option, index) => (
            <button key={index} onClick={() => handleOptionClick(option)}>
              {option}
            </button>
          ))}
        </div>
      )}
    </div>
  );
};

export default App;
